import Foundation
import UIKit
/// https://leetcode.com/problems/minimum-window-substring/
///
/// 

class Window {
    var startPosition: Int
    var endPosition: Int

    init(startPosition: Int, endPosition: Int) {
        self.startPosition = startPosition
        self.endPosition = endPosition
    }

    var count: Int {
        return startPosition + endPosition
    }

    func stepUp() {
        startPosition = startPosition + 1
        endPosition = endPosition + 1
    }

    func stepDown() {
        startPosition = startPosition - 1
        endPosition = endPosition - 1
    }
}

private class WindowSubStringSolution {
    func minWindow(_ s: String, _ t: String) -> String {

        let searchCount = t.count
        let stringCount = s.count

        if searchCount > stringCount { return "" }



        if t == s { return s }

        var windowLength = searchCount - 1

        var map: [Character: Int] = [Character: Int]()
        for ss in t {
            if let count = map[ss] {
                map[ss] = count + 1
            } else {
                map[ss] = 1
            }
        }

        if s[s.startIndex..<s.endIndex].checks(map: map) == false {
            return ""
        }

        var dict: [String: Int] = [String: Int]()

        while windowLength < s.count && dict.isEmpty {
            performCheckForWindowSize(s: s, t: t, size: windowLength, dict: &dict, map: map)
            windowLength += 1
        }

        if dict.isEmpty {
            return ""
        } else {
            return dict.first?.key ?? ""
        }

    }

    func performCheckForWindowSize(s: String, t: String, size: Int, dict: inout [String: Int], map: [Character: Int]) {
        var startIndex = s.startIndex
        var endIndex = s.index(startIndex, offsetBy: String.IndexDistance(size))
        var range = s[startIndex...endIndex]
        if range.checks(map: map) {
            dict[String(range)] = range.count
        }

        while (s.endIndex > range.index(endIndex, offsetBy: 1) && dict.isEmpty) {
            startIndex = s.index(startIndex, offsetBy: 1)
            endIndex = s.index(startIndex, offsetBy: String.IndexDistance(size))
            range = s[startIndex...endIndex]
            if range.checks(map: map) {
                dict[String(range)] = range.count
            }
            print(range)
        }
    }
}

public extension UIViewController {
    static func windowSubStringSolution() {
        let w = WindowSubStringSolution()
        print("--------------------------------")
        print(w.minWindow("", ""))
        print("--------------------------------")
    }
}

extension String.SubSequence {
    func checks(map: [Character: Int]) -> Bool {
        var map2: [Character: Int] = [Character: Int]()
        for ss in self {
            if let count = map2[ss] {
                map2[ss] = count + 1
            } else {
                map2[ss] = 1
            }
        }

        print(map)
        print(map2)

        var isTrue = false
        for c in map.keys {
            if let v1 = map[c], let v2 = map2[c] {
                isTrue = v2 >= v1
            } else {
                isTrue = false
                return false
            }
            if isTrue == false {
                return false
            }
        }

        return isTrue
    }
}
